(1, 'ireca', 'ireca', 'arrows', '.ireca.tparrows {\n	cursor:pointer;\n	background:rgba(##bg-color##);\n	min-width:##bg-size##px;\n    min-height:##bg-size##px;\n	position:absolute;\n	display:block;\n	z-index:100;\n    border-radius:50%;\n}\n.ireca.tparrows:hover {\n}\n.ireca.tparrows:before {\n	font-family: \\\"revicons\\\";\n	font-size:##arrow-size##px;\n	color:rgba(##arrowcolor##);\n	display:block;\n	line-height: ##bg-size##px;\n	text-align: center;\n    -webkit-transition: color 0.3s;\n    -moz-transition: color 0.3s;\n    transition: color 0.3s;\n    z-index:2;\n    position:relative;\n}\n.ireca.tparrows.tp-leftarrow:before {\n	content: \\\"\\\\e81f\\\";\n}\n.ireca.tparrows.tp-rightarrow:before {\n	content: \\\"\\\\e81e\\\";\n}\n.ireca.tparrows:hover:before {\n color:rgb(##hover-arrow-color##);\n      }\n.tp-title-wrap { \n  position:absolute;\n  z-index:1;\n  display:inline-block;\n  background:rgba(##bg-color##);\n  min-height:##bg-size##px;\n  line-height:##bg-size##px;\n  top:0px;\n  margin-left:30px;\n  border-radius:0px 30px 30px 0px;\n  overflow:hidden;\n  -webkit-transition: -webkit-transform 0.3s;\n  transition: transform 0.3s;\n  transform:scalex(0);  \n  -webkit-transform:scalex(0);  \n  transform-origin:0% 50%; \n   -webkit-transform-origin:0% 50%;\n}\n .ireca.tp-rightarrow .tp-title-wrap { \n   right:0px;\n   margin-right:30px;margin-left:0px;\n   -webkit-transform-origin:100% 50%;\nborder-radius:30px 0px 0px 30px;\n }\n.ireca.tparrows:hover .tp-title-wrap {\n	transform:scalex(1) scaley(1);\n  	-webkit-transform:scalex(1) scaley(1);\n}\n.ireca .tp-arr-titleholder {\n  position:relative;\n  -webkit-transition: -webkit-transform 0.3s;\n  transition: transform 0.3s;\n  transform:translatex(200px);  \n  text-transform:uppercase;\n  color:rgb(##hover-title-color##);\n  font-weight:400;\n  font-size:14px;\n  line-height:60px;\n  white-space:nowrap;\n  padding:0px 20px;\n  margin-left:10px;\n  opacity:0;\n}\n\n.ireca.tp-rightarrow .tp-arr-titleholder {\n  